        private void SetViewAndHighlight(Border activeBorder, UserControl newView, string activeColor)
        {
            mainView.Content = newView;

            // Reset all borders to default color
            var defaultColor = "#DCDCDC";
            caloriesEatenBorder.Background = new SolidColorBrush((Color)ColorConverter.ConvertFromString(defaultColor));
            stepTakenBorder.Background = new SolidColorBrush((Color)ColorConverter.ConvertFromString(defaultColor));
            sleepDurationBorder.Background = new SolidColorBrush((Color)ColorConverter.ConvertFromString(defaultColor));
            waterConsumedBorder.Background = new SolidColorBrush((Color)ColorConverter.ConvertFromString(defaultColor));

            // Highlight the selected border
            activeBorder.Background = new SolidColorBrush((Color)ColorConverter.ConvertFromString(activeColor));
        }

        private void caloriesEaten_MouseDown(object sender, MouseButtonEventArgs e)
        {
            SetViewAndHighlight(caloriesEatenBorder, new CaloriesEaten(), "#001950");
        }

        private void stepTaken_MouseDown(object sender, MouseButtonEventArgs e)
        {
            SetViewAndHighlight(stepTakenBorder, new StepsTaken(), "#C6D870");
        }

        private void sleepDuration_MouseDown(object sender, MouseButtonEventArgs e)
        {
            SetViewAndHighlight(sleepDurationBorder, new SleepDuration(), "#AE75DA");
        }

        private void waterConsumed_MouseDown(object sender, MouseButtonEventArgs e)
        {
            SetViewAndHighlight(waterConsumedBorder, new WaterConsumed(), "#77BEF0");
        }

